\text{Single loop: } \mathcal{E} = -\frac{d\Phi}{dt}

\text{N-turn coil: } \mathcal{E} = -N\frac{d\Phi}{dt}

\text{Magnitude: } |\mathcal{E}| = N\left|\frac{d\Phi}{dt}\right|

\text{Induced current: } i = \frac{|\mathcal{E}|}{R} = \frac{N}{R}\left|\frac{d\Phi}{dt}\right|

\mathcal{E} = -N\frac{d\Phi}{dt}

\text{The negative sign encodes Lenz's law:}
\text{induced emf opposes the change in flux.}

\text{If } \Phi \uparrow \Rightarrow \text{induced } B \text{ opposes increase}
\text{If } \Phi \downarrow \Rightarrow \text{induced } B \text{ supports decrease}

\text{Consequence: conservation of energy}

\mathcal{E} = Blv

\text{where: } B = \text{magnetic field, } l = \text{rod length,}
\quad\quad\quad v = \text{velocity (all mutually perpendicular)}

\text{General form: } \mathcal{E} = \int (\vec{v} \times \vec{B}) \cdot d\vec{l}

\text{Origin: magnetic force on free charges in rod}
\quad F = qvB \Rightarrow E_{induced} = vB

\mathcal{E} = \frac{1}{2}B\omega l^2

\text{where: } \omega = \text{angular velocity, } l = \text{rod length,}
\quad\quad\quad B = \text{field (perpendicular to plane of rotation)}

\text{Derivation: element } dx \text{ at distance } x \text{ has } v = \omega x
\quad d\mathcal{E} = B(\omega x)dx
\quad \mathcal{E} = \int_0^l B\omega x\, dx = \frac{B\omega l^2}{2}

q = \frac{\Delta\Phi}{R} = \frac{|\Phi_2 - \Phi_1|}{R}

\text{For N-turn coil: } q = \frac{N|\Delta\Phi|}{R}

\text{Derivation: } q = \int i\, dt = \int\frac{\mathcal{E}}{R}dt = \frac{1}{R}\int d\Phi = \frac{\Delta\Phi}{R}

\textbf{Key: } q \text{ is independent of the time taken}

\text{Definition: } \Phi = Li \quad\Rightarrow\quad \mathcal{E} = -L\frac{di}{dt}

\text{Solenoid (N turns, length } l\text{, area } A\text{):}
L = \frac{\mu_0 N^2 A}{l} = \mu_0 n^2 V

\text{where } n = N/l \text{ (turns per unit length), } V = Al

\text{Unit: Henry (H) = } \frac{\text{Wb}}{\text{A}} = \frac{\text{V}\cdot\text{s}}{\text{A}}

\text{Energy in inductor: } U = \frac{1}{2}Li^2

\text{Also: } U = \frac{\Phi^2}{2L} = \frac{1}{2}\Phi i

\text{Magnetic energy density (solenoid):}
u_B = \frac{B^2}{2\mu_0}

\text{Analogous to electric energy density: } u_E = \frac{1}{2}\epsilon_0 E^2

\text{Series (no mutual coupling):}
L_{eq} = L_1 + L_2 + L_3 + \cdots

\text{Parallel:}
\frac{1}{L_{eq}} = \frac{1}{L_1} + \frac{1}{L_2} + \frac{1}{L_3} + \cdots

\text{For two in parallel: } L_{eq} = \frac{L_1 L_2}{L_1 + L_2}

\text{n identical inductors in parallel: } L_{eq} = \frac{L}{n}

i(t) = \frac{\mathcal{E}}{R}\left(1 - e^{-t/\tau}\right)

\text{Time constant: } \tau = \frac{L}{R}

\text{At } t = 0: \quad i = 0 \quad (\text{inductor acts as open circuit})
\text{At } t = \tau: \quad i = 0.63\,\frac{\mathcal{E}}{R}
\text{As } t \to \infty: \quad i \to \frac{\mathcal{E}}{R} \quad (\text{inductor acts as wire})

i(t) = i_0\, e^{-t/\tau}

\text{Time constant: } \tau = \frac{L}{R} \quad (\text{same as growth})

\text{where } i_0 = \text{steady-state current before disconnection}

\text{At } t = \tau: \quad i = \frac{i_0}{e} \approx 0.37\, i_0

\text{Energy stored } \frac{1}{2}Li_0^2 \text{ is fully dissipated as heat}

\text{Differential equation: } L\frac{d^2q}{dt^2} + \frac{q}{C} = 0

\omega_0 = \frac{1}{\sqrt{LC}} \quad\quad f = \frac{1}{2\pi\sqrt{LC}}

q(t) = q_0 \cos(\omega_0 t + \phi)

i(t) = -\omega_0 q_0 \sin(\omega_0 t + \phi) = I_0 \sin(\omega_0 t + \phi)

\text{where } I_0 = \omega_0 q_0 = q_0 / \sqrt{LC}

\Phi_{21} = M\, i_1 \quad\Rightarrow\quad \mathcal{E}_2 = -M\frac{di_1}{dt}

\text{Coaxial solenoids (N}_1, N_2 \text{ turns, length } l\text{, area } A\text{):}
M = \frac{\mu_0 N_1 N_2 A}{l}

\text{Reciprocity: } M_{12} = M_{21} = M

\text{Relation to self-inductances:}
M = k\sqrt{L_1 L_2} \quad (k = \text{coupling coefficient})\

k = \frac{M}{\sqrt{L_1 L_2}}

\text{Range: } 0 \leq k \leq 1

k = 0 \Rightarrow \text{no flux linkage (coils perpendicular)}
k = 1 \Rightarrow \text{perfect coupling (all flux links both coils)}

\text{Maximum mutual inductance: } M_{max} = \sqrt{L_1 L_2}
\text{(achieved when coils are coaxial)}

\Phi(t) = NBA\cos(\omega t)

\mathcal{E}(t) = -\frac{d\Phi}{dt} = NBA\omega\sin(\omega t)

\mathcal{E}_{peak} = NBA\omega = \mathcal{E}_0

\text{where: } N = \text{turns, } B = \text{field, } A = \text{coil area,}
\quad\quad\quad \omega = \text{angular velocity}

\mathcal{E} = 0 \text{ when coil is } \perp \text{ to } B \text{ (max flux)}
\mathcal{E} = \mathcal{E}_0 \text{ when coil is } \| \text{ to } B \text{ (zero flux)}

\oint \vec{E} \cdot d\vec{l} = -\frac{d\Phi_B}{dt}

\text{For circular path of radius } r \text{ (cylindrical symmetry):}
E(2\pi r) = -\frac{d\Phi_B}{dt}

\text{Inside a cylinder of radius } R \text{ (at } r < R\text{):}
E = \frac{r}{2}\frac{dB}{dt}

\text{Outside (at } r > R\text{):}
E = \frac{R^2}{2r}\frac{dB}{dt}
