h\nu = W_0 + KE_{max}

\text{where: } h\nu = \text{photon energy, } W_0 = \text{work function}

KE_{max} = \frac{1}{2}mv_{max}^2 = h\nu - W_0

\text{Threshold frequency: } \nu_0 = \frac{W_0}{h}

\text{Maximum wavelength: } \lambda_{max} = \frac{hc}{W_0}

\textbf{Photon energy shortcut: } E = \frac{12400}{\lambda(\text{in } \AA)} \text{ eV}

eV_c = KE_{max} = h\nu - W_0

\Rightarrow V_c = \frac{h}{e}\nu - \frac{W_0}{e} = \frac{h}{e}(\nu - \nu_0)

\text{Slope of } V_c \text{ vs } \nu \text{ graph: } \frac{h}{e} \text{ (same for all metals)}

\textbf{Key facts:}
\text{- } V_c \text{ is independent of light intensity}
\text{- } V_c \text{ increases linearly with frequency}
\text{- Intercept on } \nu\text{-axis gives } \nu_0 \text{ (threshold frequency)}

\lambda = \frac{h}{p} = \frac{h}{mv} = \frac{h}{\sqrt{2mKE}}

\text{Electron accelerated through } V \text{ volts:}
\lambda_e = \frac{12.27}{\sqrt{V}} \text{ Å}

\text{Proton accelerated through } V \text{ volts:}
\lambda_p = \frac{0.286}{\sqrt{V}} \text{ Å}

\text{General: } \lambda = \frac{h}{\sqrt{2mqV}}
\text{where } q = \text{charge of particle}

\text{Radius of nth orbit:}
r_n = \frac{n^2 \hbar^2}{4\pi^2 Kme^2 Z} = 0.53\frac{n^2}{Z} \text{ Å}

\text{Velocity in nth orbit:}
v_n = \frac{2\pi K Z e^2}{n\hbar} = 2.18 \times 10^6 \cdot \frac{Z}{n} \text{ m/s}

\text{Angular momentum (quantization condition):}
L = n\hbar = \frac{nh}{2\pi}

\text{Note: } r_n \propto n^2/Z \quad v_n \propto Z/n

E_n = -\frac{13.6 Z^2}{n^2} \text{ eV/atom}

\text{Binding energy in nth orbit:}
BE = \frac{13.6 Z^2}{n^2} \text{ eV}

\text{Ionization energy (from ground state):}
IE = 13.6 Z^2 \text{ eV}

\text{Energy of emitted photon:}
\Delta E = 13.6 Z^2 \left(\frac{1}{n_1^2} - \frac{1}{n_2^2}\right) \text{ eV} \quad (n_2 > n_1)

\text{For H: } E_1 = -13.6 \text{ eV, } E_2 = -3.4 \text{ eV, } E_3 = -1.51 \text{ eV}

\frac{1}{\lambda} = RZ^2\left(\frac{1}{n_1^2} - \frac{1}{n_2^2}\right)

\text{where } R = 1.097 \times 10^7 \text{ m}^{-1} = 109677 \text{ cm}^{-1}

\text{Spectral series for hydrogen (Z=1):}
\text{Lyman: } n_1=1, \text{ UV region}
\text{Balmer: } n_1=2, \text{ visible region}
\text{Paschen: } n_1=3, \text{ IR region}
\text{Brackett: } n_1=4, \text{ far IR}
\text{Pfund: } n_1=5, \text{ far IR}

\text{Series limit: } \lambda_{limit} = \frac{n_1^2}{RZ^2}

\textbf{Moseley's Law:}
\sqrt{\nu} = a(Z - b)

\text{For K}_\alpha \text{ series: } b = 1,\quad a = 4.98 \times 10^7 \sqrt{\text{Hz}}

\textbf{Bragg's Law (X-ray diffraction):}
2d\sin\theta = n\lambda

\text{where } d = \text{interplanar spacing, } \theta = \text{glancing angle}

\textbf{Minimum wavelength (cutoff):}
\lambda_{min} = \frac{hc}{eV} = \frac{12400}{V(\text{in volts})} \text{ Å}

\textbf{Mass Defect:}
\Delta m = [Zm_p + (A-Z)m_n] - m_{nucleus}

\textbf{Binding Energy:}
BE = \Delta m \times c^2 = \Delta m \times 931.5 \text{ MeV}

\text{(since } 1 \text{ amu} = 931.5 \text{ MeV/}c^2\text{)}

\textbf{Binding Energy per Nucleon:}
\frac{BE}{A} = \text{measure of nuclear stability}

\text{Maximum } \frac{BE}{A} \approx 8.8 \text{ MeV at } A \approx 56 \text{ (Fe-56)}

\text{Decay rate equation:} \quad \frac{dN}{dt} = -\lambda N

\text{Number of nuclei at time } t\text{:}
N_t = N_0 e^{-\lambda t}

\text{Activity at time } t\text{:}
R(t) = \lambda N_t = \lambda N_0 e^{-\lambda t} = R_0 e^{-\lambda t}

\text{Units: } 1 \text{ Bq} = 1 \text{ decay/s}
\quad\quad\quad 1 \text{ Ci} = 3.7 \times 10^{10} \text{ Bq}

\textbf{Half-life:}
t_{1/2} = \frac{\ln 2}{\lambda} = \frac{0.693}{\lambda}

\text{After } n \text{ half-lives: } N = \frac{N_0}{2^n}

\textbf{Mean (average) life:}
\tau = \frac{1}{\lambda} = \frac{t_{1/2}}{\ln 2} = 1.44 \times t_{1/2}

\textbf{Relationship:}
\lambda = \frac{0.693}{t_{1/2}} = \frac{1}{\tau}

\text{Simultaneous decays: } \lambda_{total} = \lambda_1 + \lambda_2

\text{Activity } A = \frac{dN}{dt} = \lambda N

\text{At time } t\text{: } A(t) = A_0 e^{-\lambda t}

\text{where } A_0 = \lambda N_0 \text{ (initial activity)}

\text{After } n \text{ half-lives: } A = \frac{A_0}{2^n}

\text{Relation to number of nuclei:}
N = \frac{A}{\lambda} = A \cdot \tau = A \cdot \frac{t_{1/2}}{0.693}

\text{Units: Becquerel (Bq) or Curie (Ci)}

\text{For reaction: } a + X \rightarrow Y + b

Q = (m_a + m_X - m_Y - m_b) \times 931.5 \text{ MeV}

Q = (m_{initial} - m_{final}) \times c^2

\text{If } Q > 0 \text{: Exothermic (energy released, spontaneous)}
\text{If } Q < 0 \text{: Endothermic (energy required)}

\textbf{Threshold KE for endothermic reaction:}
KE_{th} = |Q| \cdot \frac{m_a + m_X + m_Y + m_b}{2m_X}

E = mc^2

\text{Rest mass energy of electron:}
m_e c^2 = 0.511 \text{ MeV}

\text{1 atomic mass unit:}
1 \text{ amu} = 1.66 \times 10^{-27} \text{ kg} = 931.5 \text{ MeV}/c^2

\textbf{Pair production threshold:}
E_{photon} \geq 2m_e c^2 = 1.022 \text{ MeV}

\textbf{Annihilation:}
e^- + e^+ \rightarrow 2\gamma \quad (\text{each } \gamma \text{ carries } 0.511 \text{ MeV})

\Delta\lambda = \lambda' - \lambda = \frac{h}{m_e c}(1 - \cos\theta)

\text{Compton wavelength of electron:}
\lambda_c = \frac{h}{m_e c} = 2.43 \times 10^{-12} \text{ m} = 0.0243 \text{ Å}

\text{Maximum shift (backscatter, } \theta = 180°\text{):}
\Delta\lambda_{max} = \frac{2h}{m_e c} = 2\lambda_c = 0.0486 \text{ Å}

\text{Forward scatter (} \theta = 0°\text{): } \Delta\lambda = 0

\text{Scattered photon energy: } E' = \frac{E}{1 + \frac{E}{m_e c^2}(1-\cos\theta)}

\textbf{Nuclear radius:}
R = R_0 A^{1/3} \quad \text{where } R_0 = 1.2 \times 10^{-15} \text{ m}

\text{Volume: } V \propto A \quad (\text{proportional to mass number})

