\text{Magnitude of resultant:}
R = \sqrt{P^2 + Q^2 + 2PQ\cos\theta}

\text{Direction (angle with first vector):}
\tan\alpha = \frac{Q\sin\theta}{P + Q\cos\theta}

\text{where } \theta = \text{angle between } \vec{P} \text{ and } \vec{Q}

\textbf{Special cases:}
\text{Same direction (}\theta=0\text{): } R_{max} = P + Q
\text{Opposite (}\theta=180°\text{): } R_{min} = |P - Q|

\text{Resolution into components:}
a_x = a\cos\theta \quad a_y = a\sin\theta

\text{Reconstruction:}
a = \sqrt{a_x^2 + a_y^2}

\tan\theta = \frac{a_y}{a_x}

\text{Component form:}
\vec{a} = a_x\hat{i} + a_y\hat{j} = a\cos\theta\,\hat{i} + a\sin\theta\,\hat{j}

\text{For multiple vectors: } R_x = \sum a_{xi},\quad R_y = \sum a_{yi}

\text{Magnitude and direction:}
\vec{a}\times\vec{b} = ab\sin\theta\,\hat{n}

|\vec{a}\times\vec{b}| = ab\sin\theta

\text{Anti-commutative: } \vec{a}\times\vec{b} = -(\vec{b}\times\vec{a})

\text{Unit vectors: } \hat{i}\times\hat{j}=\hat{k},\;\hat{j}\times\hat{k}=\hat{i},\;\hat{k}\times\hat{i}=\hat{j}
\hat{i}\times\hat{i} = 0

\text{Component form (3D):}
\vec{a}\times\vec{b} = (a_y b_z - a_z b_y)\hat{i} + (a_z b_x - a_x b_z)\hat{j} + (a_x b_y - a_y b_x)\hat{k}

\text{Area of parallelogram} = |\vec{a}\times\vec{b}|

\text{Velocity: } [v] = LT^{-1}
\text{Acceleration: } [a] = LT^{-2}
\text{Force: } [F] = MLT^{-2}
\text{Work / Energy: } [W] = ML^2T^{-2}
\text{Power: } [P] = ML^2T^{-3}
\text{Pressure: } [p] = ML^{-1}T^{-2}
\text{Momentum: } [p] = MLT^{-1}
\text{Angular momentum: } [L] = ML^2T^{-1}
\text{Torque: } [\tau] = ML^2T^{-2}
\text{Frequency: } [f] = T^{-1}

\text{For a quantity with dimension } [M^a L^b T^c]:

n_2 = n_1 \left[\frac{M_1}{M_2}\right]^a \left[\frac{L_1}{L_2}\right]^b \left[\frac{T_1}{T_2}\right]^c

\textbf{Key conversions:}
\text{1 Newton} = 10^5 \text{ Dyne}
\text{1 Joule} = 10^7 \text{ Erg}
\text{1 MeV} = 1.6\times10^{-13}\text{ J}
\text{1 horse power} = 746\text{ W}
\text{1 \AA ngstr\ddot{o}m} = 10^{-10}\text{ m}

\textbf{Sum / Difference: } x = a \pm b
\Delta x = \pm(\Delta a + \Delta b) \quad \text{(absolute errors add)}

\textbf{Product / Division: } x = \frac{ab}{c}
\frac{\Delta x}{x} = \pm\left(\frac{\Delta a}{a} + \frac{\Delta b}{b} + \frac{\Delta c}{c}\right) \quad \text{(relative errors add)}

\textbf{Power: } x = a^n b^m
\frac{\Delta x}{x} = \pm\left(n\frac{\Delta a}{a} + m\frac{\Delta b}{b}\right)

\text{Percentage error} = \frac{\Delta x}{x}\times 100\%

\textbf{General power rule:}
\frac{\Delta f}{f} = n_1\frac{\Delta a}{a} + n_2\frac{\Delta b}{b} + \cdots

\textbf{Example — Density } \rho = \frac{M}{L^3}:
\frac{\Delta\rho}{\rho} = \frac{\Delta M}{M} + 3\frac{\Delta L}{L}

\textbf{Example — Gravitational acceleration } g = \frac{4\pi^2 L}{T^2}:
\frac{\Delta g}{g} = \frac{\Delta L}{L} + 2\frac{\Delta T}{T}

\text{Quantity with highest power contributes most to error.}

\textbf{Power rule: } \frac{d}{dx}(x^n) = nx^{n-1}

\textbf{Trig: } \frac{d}{dx}(\sin x) = \cos x,\quad \frac{d}{dx}(\cos x) = -\sin x

\textbf{Exponential / Log:}
\frac{d}{dx}(e^x) = e^x,\quad \frac{d}{dx}(\ln x) = \frac{1}{x}

\textbf{Product rule: } \frac{d}{dx}(uv) = u\frac{dv}{dx} + v\frac{du}{dx}

\textbf{Chain rule (linear): } \frac{d}{dx}[f(ax+b)] = a\cdot f'(ax+b)

\textbf{Sum rule: } \frac{d}{dx}[u+v] = \frac{du}{dx} + \frac{dv}{dx}

\textbf{Least count:}
L.C. = 1\;M.S.D. - 1\;V.S.D.

\text{Typical: } L.C. = 1\text{ mm} - 0.9\text{ mm} = 0.1\text{ mm} = 0.01\text{ cm}

\textbf{Reading formula:}
\text{Reading} = N + n \times L.C.

\text{where } N = \text{main scale reading (left of Vernier zero)}
\text{and } n = \text{Vernier division that coincides with M.S.}

\textbf{True reading:}
\text{True} = \text{Measured} - \text{Zero error}

\textbf{Least count:}
L.C. = \frac{\text{Pitch}}{\text{Number of circular scale divisions}}

\text{Typical: } L.C. = \frac{1\text{ mm}}{100} = 0.01\text{ mm} = 10\;\mu\text{m}

\textbf{Reading formula:}
\text{Reading} = M.S.R. + n \times L.C.

\text{where } M.S.R. = \text{linear (sleeve) scale reading}
\text{and } n = \text{circular scale division aligned with reference line}

\textbf{True reading:}
\text{True} = \text{Measured} - \text{Zero error}

\textbf{Balance condition (Wheatstone bridge):}
\frac{R}{S} = \frac{l}{100 - l}

\textbf{Unknown resistance:}
R = S \times \frac{l}{100 - l}

\text{where } l = \text{balance point from end A (in cm)}
\text{and } S = \text{known resistance in other gap}

\textbf{Minimum error: } l = 50\text{ cm}

\textbf{Error in R:}
\frac{\Delta R}{R} = \frac{\Delta l}{l(100 - l)}\times 100\

\textbf{Potentiometer principle:}
\text{The potential difference across a uniform wire is}
\text{proportional to its length:}
V \propto l \quad\Rightarrow\quad \frac{V_1}{V_2} = \frac{l_1}{l_2}

\textbf{Applications: } E.M.F. comparison, internal resistance

\textbf{Resonance Column — speed of sound:}
v = 2f_0(l_2 - l_1)

\text{where } l_1, l_2 = \text{first and second resonance lengths}
\text{and } f_0 = \text{tuning fork frequency}

\textbf{Time period:}
T = 2\pi\sqrt{\frac{L}{g}}

\textbf{Gravitational acceleration:}
g = \frac{4\pi^2 L}{T^2}

\textbf{Error in g:}
\frac{\Delta g}{g} = \frac{\Delta L}{L} + 2\frac{\Delta T}{T}

\text{To minimise } \Delta T: \text{ measure } n \text{ oscillations,}
T = \frac{\text{total time}}{n},\quad \Delta T = \frac{\Delta t}{n}

\text{Amplitude must be small (} < 15°\text{) for SHM approximation.}
